		<div id="loginbox">			<a href="#" id="logo"><?php echo APP_NAME?></a>			<div id="loginform">				<form name="frm" action="<?php echo base_url()?>admin/login" method="POST">					<?php if(isset($msgError) && $msgError != "") echo '<div class="error "><h3>'.$msgError.'</h3><a class="hide_btn" href="#">&nbsp;</a></div>';?>					<div id="username_field"><input type="text" name="txtUsername" placeholder="Usuario" class="required" value="" autocomplete="off"/></div>					<div id="password_field"><input type="password" name="txtPassword" placeholder="ContraseÃ±a" class="required" value="" autocomplete="off"/></div>					<div id="buttonline">						<input type="submit" id="loginbutton" class="float_right width_4" value="Acceder" />					</div>				</form>			</div>		</div>	</body></html>